import UIKit
import Swifter
import SafariServices

class AddShortcut {

    private var localServer: HttpServer?

    func showShortcutScreen(appID: Int, appName: String) {

        self.localServer = HttpServer()

        let urlStr = "mallet://r/\(appID)"

        let port = UInt16.random(in: 49152...65535)

        guard let shortcutURL = URL(string: "http://localhost:\(port)/shortcut") else {
            return
        }

        let html = self.generateHTML(icon: "", title: appName, url: urlStr)

        #if DEBUG
            print(html)
        #endif

        guard let base64 = html.data(using: .utf8)?.base64EncodedString() else {
            return
        }

        self.localServer?["/shortcut"] = { request in
            .movedPermanently("data:text/html;base64,\(base64)")
        }

        try? self.localServer?.start(port)

        UIApplication.shared.open(shortcutURL)
    }

    private func generateHTML(icon: String, title: String, url: String) -> String {
        """
        <html>
          <head>
            <meta charset="UTF-8">
            <meta name="apple-mobile-web-app-capable" content="yes" />
            <meta name="apple-mobile-web-app-status-bar-style" content="white" />
            <link
              rel="apple-touch-icon"
              href="data:image/png;base64,"
            />
            <title>
              \(title)
            </title>
          </head>
          <body>
            <a id="redirect" href="\(url)"></a>
            <div id="content"></div>
            <script type="text/javascript">
              if (navigator.standalone) {
                var e = document.getElementById("redirect");
                var v = document.createEvent("MouseEvents");
                v.initEvent("click", true, true);
                e.dispatchEvent(v);
              } else {
                var div = document.createElement("div");
                div.style.textAlign = "center";
                div.style.fontFamily = "sans-serif";

                {
                  var space1 = document.createElement("div");
                  space1.style.height = "30px";

                  var appName = document.createElement("p");
                  appName.textContent = "\(title)";
                  appName.style.fontSize = "50px";

                  var space2 = document.createElement("div");
                  space2.style.height = "50px";

                  div.appendChild(space1);
                  div.appendChild(appName);
                  div.appendChild(space2);
                }

                {
                  var div2 = document.createElement("div");
                  div2.style.fontSize = "40px";

                  {
                    var image = document.createElement("img");
                    image.src =
                      "data:image/png;base64,";
                    div2.appendChild(image);

                    var label = document.createElement("p");
                    var buttonImage = document.createElement("img");
                    buttonImage.src =
                      "data:image/png;base64,";
                    buttonImage.style.width = "40px";
                    label.append("1. Tap ");
                    label.append(buttonImage);
                    label.append(" in the toolbar");
                    div2.appendChild(label);
                  }

                  {
                    var image = document.createElement("img");
                    image.src =
                      "data:image/png;base64,";
                    div2.appendChild(image);

                    var label = document.createElement("p");
                    label.textContent = '2. Tap "Add to Home Screen"';
                    div2.appendChild(label);
                  }

                  {
                    var image = document.createElement("img");
                    image.src =
                      "data:image/png;base64,";
                    div2.appendChild(image);

                    var label = document.createElement("p");
                    label.textContent = '2. Tap "Add"';
                    div2.appendChild(label);
                  }

                  div.appendChild(div2);
                }

                {
                  var space1 = document.createElement("div");
                  space1.style.height = "50px";
                  div.appendChild(space1);

                  var div3 = document.createElement("div");
                  div3.style.fontSize = "40px";

                  var link = document.createElement("a");
                  link.textContent = "mallet.app";
                  link.href = "https://mallet.app/";
                  div3.appendChild(link);

                  div.appendChild(div3);
                }

                document.getElementById("content").appendChild(div);
              }
            </script>
          </body>
        </html>
        """
    }

}
